$(function(){$("#carousel-answer").hammer().on("swipeleft",function(){page_Btn()});$("#carousel-answer").hammer().on("swiperight",function(){pageL_Btn()})});function answerSheet(){$("#J_myAnswer_alert").show();$("#J_myAnswer").hide()}function contiTopic(a){var b=confirm(a);if(b==false){$("#carousel-answer").carousel(0)}else{return true}}function page_Btn(){$(".js_lastPage").parent().show();var b=$(".js_answer_page.active").find(".js_num").text().trim();var a=$(".js_answer_page").length;if(b==a){$(".js_nextPage").parent().hide();$(".js_submitPage").parent().show()}else{$("#carousel-answer").carousel("next");if(b==a-1){$(".js_nextPage").parent().hide();$(".js_submitPage").parent().show()}else{$(".js_nextPage").parent().show();$(".js_submitPage").parent().hide()}}}function pageL_Btn(){$(".js_nextPage").parent().show();$(".js_submitPage").parent().hide();var b=$(".js_answer_page.active").find(".js_num").text().trim();var a=$(".js_answer_page").length;if(b==1){$(".js_lastPage").hide()}else{$("#carousel-answer").carousel("prev");if(b==2){$(".js_lastPage").parent().hide()}else{$(".js_lastPage").parent().show()}}};